mapper.select:
SELECT * FROM (

) R

	rnum BETWEEN #{startNo } AND #{endNo }

SELECT mf.article_no as article_no , mf.article_title as article_title , mf.article_content as article_content , mf.board_no as board_no , mf.party_location as party_location , mf.user_no as user_no , mf.category as category , mf.meet_time as meet_time , mf.mate_list as mate_list , u.user_id as user_id , u.user_nick as user_nick , u.user_profilestored as user_profilestored FROM tb_board2 mf JOIN tb_user2 u ON u.user_no = mf.user_no WHERE mf.board_no = 2 AND is_delete = 0
AND SUBSTR(party_location, 0, INSTR(party_location, ' ', 1, 1) - 1) = #{party_location }
AND meet_time = #{meet_time }
AND category = #{category }
ORDER BY article_no DESC

SELECT * FROM ( SELECT rownum rnum, B.* FROM ( SELECT mf.article_no as article_no , mf.article_title as article_title , mf.article_content as article_content , mf.board_no as board_no , mf.party_location as party_location , mf.user_no as user_no , mf.category as category , mf.meet_time as meet_time , mf.mate_list as mate_list , u.user_id as user_id , u.user_nick as user_nick , u.user_profilestored as user_profilestored FROM tb_board2 mf JOIN tb_user2 u ON u.user_no = mf.user_no WHERE mf.board_no = 2 AND is_delete = 0
AND SUBSTR(party_location, 0, INSTR(party_location, ' ', 1, 1) - 1) = #{party_location }
AND meet_time = #{meet_time }
AND category = #{category }
ORDER BY article_no DESC ) B ORDER BY rnum ) R
rnum BETWEEN #{startNo } AND #{endNo }

SELECT article_no , article_title , article_content , article_hit , board_no , party_location , user_no , category
, TO_CHAR(meet_time, 'YYYY-MM-DD HH24:MI') as meet_time
, mate_list FROM tb_board2 WHERE board_no = 2 AND article_no = #{article_no }

mapper.update:
UPDATE tb_board2
SET article_title = #{article_title }
	, article_content = #{article_content }
	, party_location = #{party_location }
	, category = #{category }
	, meet_time = TO_DATE(#{meet_time}, 'YYYY-MM-DD HH24MI')
	, mate_list = #{mate_list}
WHERE board_no = 2
	AND article_no = #{article_no }

mapper.select:
SELECT count(*) FROM tb_recommend2
WHERE board_no = 2
	AND article_no = #{article_no }

mapper.delete:
DELETE FROM tb_recommend2
WHERE user_no = #{user_no}
	AND board_no = 2
	AND article_no = #{article_no }

