mapper.select:

SELECT * FROM
(SELECT rownum rnum, list.* FROM 
(SELECT user_no, article_no, article_title, article_content, create_date, meet_time, article_hit, is_delete,board_no
    ,(SELECT user_nick FROM tb_user u WHERE u.user_no = b.user_no) user_nick
    ,(SELECT count(*) FROM tb_comment c WHERE b.board_no = #{board_no} AND b.article_no = c.article_no ) cnt_comment
FROM tb_board b
ORDER BY article_no DESC) list


	AND board_no = #{board_no}


	AND is_delete = #{is_delete}


	AND ${searchCategory} LIKE '%${searchKeyword}%'


) WHERE rnum BETWEEN #{startNo} AND #{endNo}

SELECT count(*) FROM (SELECT b.* ,(SELECT user_nick FROM tb_user u WHERE b.user_no = u.user_no) user_nick FROM tb_board b)
AND board_no = #{searchBoard_no}
AND ${searchCategory} LIKE '%${searchKeyword}%'
