mapper.select:
SELECT * FROM ( SELECT rownum rnum, I.* FROM ( SELECT article_no , article_title , article_content , board_no , user_no , create_date , revision_date , is_delete , article_hit , post_group , post_step , post_indent , (SELECT user_nick FROM tb_user2 u WHERE u.user_no = b.user_no) user_nick FROM tb_board2 b WHERE board_no = #{board_no}
ORDER BY post_group DESC, post_step ASC ) I ORDER BY rnum )S
rnum BETWEEN #{startNo } AND #{endNo }

mapper.update:
UPDATE tb_board2
SET article_hit = article_hit + 1
WHERE board_no = #{board_no} 
	AND article_no = #{article_no}

UPDATE tb_board2
SET article_title = #{article_title}, article_content = #{article_content}, revision_date = sysdate
WHERE article_no = #{article_no}
AND board_no = #{board_no}

UPDATE tb_board2
	SET is_delete = 1
WHERE article_no = #{article_no}
AND board_no = #{board_no}

mapper.select:
SELECT * FROM tb_board2
WHERE board_no = #{board_no}
	AND article_no = #{article_no}
