mapper.update:
UPDATE tb_user2
	SET user_nick = #{user_nick}, user_email = #{user_email}, user_pw = #{user_pw}
		,user_profileorigin = #{user_profileorigin}, user_profilestored = #{user_profilestored }
WHERE user_no = #{user_no}

mapper.select:
SELECT COUNT(*) FROM tb_user2
WHERE 1=1
 AND user_id = #{user_id}
 AND user_pw = #{user_pw}

mapper.delete:
DELETE FROM tb_user2
WHERE user_id = #{user_id}
AND user_pw = #{user_pw }

mapper.select:
SELECT count(*) from( 
	SELECT * FROM tb_board2
	WHERE user_no = #{user_no}


)CNT

SELECT * FROM (
		SELECT rownum rnum, B.*
		FROM(
			SELECT board.article_title, board.article_content, board.create_date , board.article_no, board.board_no, is_delete
			, (SELECT board_name FROM tb_boardclass2 WHERE board_no = board.board_no) board_name
			FROM tb_board2 board
			WHERE user_no = #{user_no}



			ORDER BY article_no DESC

		) B
		ORDER BY rnum
) R

	rnum BETWEEN #{startNo } AND #{endNo }

mapper.update:
UPDATE tb_board2 SET is_delete = 1

WHERE article_no IN

	#{article_no}

mapper.select:
SELECT count(*) from( 
	SELECT * FROM tb_comment2
	WHERE user_no = #{user_no}

			AND comment_content Like '%'||#{postSearch}||'%'


)CNT

SELECT * FROM ( SELECT rownum rnum, B.* FROM( SELECT cmmt.comment_content, cmmt.comment_no, cmmt.create_date, cmmt.board_no, cmmt.article_no, cmmt.is_delete , (SELECT board_name FROM tb_boardclass2 WHERE board_no = cmmt.board_no) board_name , (SELECT article_title FROM tb_board2 WHERE article_no = cmmt.article_no) article_title FROM tb_comment2 cmmt WHERE user_no = #{user_no}
AND comment_content Like '%'||#{postSearch}||'%'
ORDER BY article_no DESC
) B ORDER BY rnum ) R
rnum BETWEEN #{startNo } AND #{endNo }

mapper.update:
UPDATE tb_comment2 SET is_delete = 1

WHERE comment_no IN

	#{comment_no}
