mapper.select:
SELECT * FROM (SELECT rownum rnum, list.* FROM (SELECT user_no , article_no , article_title , article_content , party_location , create_date , meet_time , article_hit , is_delete , board_no ,(SELECT user_nick FROM tb_user2 u WHERE u.user_no = b.user_no) user_nick ,(SELECT count(*) FROM tb_comment2 c WHERE b.board_no = #{board_no} AND b.article_no = c.article_no AND c.comment_order = 0) cnt_comment ,(SELECT count(*) FROM tb_recommend2 r WHERE b.board_no = #{board_no} AND b.article_no = r.article_no) cnt_recommend FROM tb_board2 b ORDER BY article_no DESC) list
AND board_no = #{board_no}
AND is_delete = #{is_delete}
AND ${searchCategory} LIKE '%${searchKeyword}%'
AND party_location = #{searchLocation}
) WHERE rnum BETWEEN #{startNo} AND #{endNo}

SELECT count(*) FROM (SELECT b.* ,(SELECT user_nick FROM tb_user2 u WHERE b.user_no = u.user_no) user_nick FROM tb_board2 b)
AND board_no = #{searchBoard_no}
AND ${searchCategory} LIKE '%${searchKeyword}%'
AND party_location = #{searchLocation}

mapper.update:
		UPDATE tb_board2
		SET article_hit = article_hit + 1
		WHERE board_no = #{board_no}
   			AND article_no = #{article_no}

mapper.select:
SELECT file_storedname FROM tb_file2
WHERE article_no = #{article_no}
	AND board_no = #{board_no}

mapper.update:
UPDATE tb_board2
	SET article_title = #{article_title}
	,article_content = #{article_content}
	,party_location = #{party_location, jdbcType=VARCHAR}
	,revision_date = sysdate
	,meet_time = to_date(#{meet_time}, 'YYYY-MM-DDHH24MI')
WHERE 
	article_no = #{article_no}
	AND board_no = #{board_no}

UPDATE tb_board2
SET is_delete = 1
WHERE
	article_no = #{article_no}
	AND board_no = #{board_no}

mapper.select:
SELECT count(*) FROM tb_recommend2
WHERE article_no = #{article_no}
	AND board_no = #{board_no}
	AND user_no = #{user_no}

mapper.delete:
DELETE tb_recommend2
WHERE article_no = #{article_no}
	AND board_no = #{board_no}
	AND user_no = #{user_no}

mapper.select:
SELECT count(*) FROM tb_recommend2
WHERE article_no = #{article_no}
	AND board_no = #{board_no}
