mapper.select:
select * from(
	select rownum rnum, B.* from(

	) B
	order by rnum
)
where rnum between #{startNo} and #{endNo}

select * from(
	select rownum rnum, B.* from(
		select * from tb_userreport2
		where is_processed = 0 
		order by user_no
	)B
	order by rnum
)
where rnum between #{startNo} and #{endNo}

select * from(
	select rownum rnum, B.* from(
		select * from tb_userreport2
		where is_processed = 0
		order by user_no desc
	)B
	order by rnum
)
where rnum between #{startNo} and #{endNo}

select * from(
	select rownum rnum, B.* from(
		select * from tb_userreport2
		where is_processed = 0 
		order by report_time
	)B
	order by rnum
)
where rnum between #{startNo} and #{endNo}

select * from(
